import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { withFirebase } from '../Firebase';
import FirebaseContext from '../Session/context';
import * as ROUTES from '../../constants/routes';

const SignUpPage = () => (
    <div>
        <h1>SignUp</h1>
        <FirebaseContext.Consumer>
            {firebase => <SignUpForm firebase={firebase} />} 
        </FirebaseContext.Consumer>
        <SignUpForm />
    </div>
    );

async function getNewUser(username, email, user_id) {
    const configs = {
        method: 'post',
        body: JSON.stringify({
        "username": username,
        "email": email,
        "user_id": user_id
        }),
        headers: {"Content_Type": "application/json"}

    }

    const timestamp = Date.now();
    const last_login = timestamp

    const response = await fetch("http://localhost:5000/api/users_add", configs)
    const userNew = await response.json();
    console.log(userNew)

}

const INITIAL_STATE = {
    username: '',
    email: '',
    passwordOne: '',
    passwordTwo: '',
    error: null,
};

class SignUpFormBase extends Component {
    constructor(props) {
        super(props);

        this.state = {...INITIAL_STATE };
    }

    onSubmit = event => {
        const { username, email, passwordOne } = this.state;

        this.props.firebase
            .doCreateUserWithEmailAndPassword(email, passwordOne)
            .then(auth => {
            console.log(auth)


                // return jsonify({"username": username, 
                //     "email": email, "user_id": user_id

                //     })

                // Create a user in your Firebase realtime database

                return this.props.firebase
                    .db.collection("users").doc(auth.user.uid)
                    // .user(auth.currentUser.uid)
                    // .user(authUser.user.uid)
                    .set({
                        username: username,
                        email: email,
                        last_login: 1

                    });

                // return getNewUser(username, email, auth.currentUser.uid);


                })
                .then(() => {
                    this.setState({...INITIAL_STATE });
                    this.props.history.push(ROUTES.HOME);
                })
                .catch(error => {
                    this.setState({ error});
                });
            event.preventDefault();
            };


    onChange = event => {
        this.setState({ [event.target.name]: event.target.value });
    };

    render() {
        const { 
            username,
            email,
            passwordOne,
            passwordTwo,
            error, 
        } = this.state;

        const isInvalid =
        passwordOne !== passwordTwo ||
        passwordOne === '' ||
        email === '' ||
        username === '';




    return (
        <form onSubmit={this.onSubmit}>
            <input
                name="username"
                value={username}
                onChange={this.onChange}
                type="text"
                placeholder="Full Name"
            />
            <input
                name="email"
                value={email}
                onChange={this.onChange}
                type="text"
                placeholder="Email Address"
            />
            <input
                name="passwordOne"
                value={passwordOne}
                onChange={this.onChange}
                type="password"
                placeholder="Password"
                />
            <input
                name="passwordTwo"
                value={passwordTwo}
                onChange={this.onChange}
                type="password"
                placeholder="Confirm Password"
                />
            <button type="submit">Sign Up</button>
            <button disabled={isInvalid} type="submit">Sign Up</button>

            {error && <p>{error.message}</p>}
        </form>
        );
    }

}
const SignUpLink = () => (
    <p>
        Don't have an account? <Link to={ROUTES.SIGN_UP}>Sign Up</Link>
    </p>
    );

const SignUpForm = withRouter(withFirebase(SignUpFormBase));

export default SignUpPage;

export { SignUpForm, SignUpLink };
