// import Login from './components/Login';

export default function Signup() {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [createUserName, setCreateUsername] = useState("");
    const [createPassword, setCreatePassword] = useState("");


    function firstNameChangeHandler(e) {
        setFirstName(e.target.value)
    }

    function lastNameChangeHandler(e) {
        setLastName(e.target.value)
    }

    function emailChangeHandler(e) {
        setEmail(e.target.value)
    }

    function userNameChangeHandler(e) {
        setCreateUsername(e.target.value)
    }

    function passwordChangeHandler(e) {
        setCreatePassword(e.target.value)
    }

    const sendSignin = async () => {
        const newUserData = JSON.stringify({
            firstname: firstName,
            lastname: lastName,
            email: email,
            username: createUserName,
            password: createPassword
        })
        const config = {
            method: "POST",
            headers: {"Content_Type": "application/json"},
            body: newUserData

        }
        const response = await fetch("http://localhost:8080/api/Login", config);
        const data = await response.json()
        console.log(data); 

        }



    return (
        <form action="action_page.php">
            <label for="fname">first name:</label>
            <input type="text" id="fname" name="fname" onClick={firstNameChangeHandler}></input>
            <label for="lname">last name:</label>
            <input type="text" id="lname" name="lname" onClick={lastNameChangeHandler}></input>
            <label for="email">email:</label>
            <input type="text" id="email" name="email" onClick={emailChangeHandler}></input>
            <label for="cname">username:</label>
            <input type="text" id="cname" name="cname" onClick={userNameChangeHandler}></input>
            <label for="cpassword">password:</label>
            <input type="text" id="cpassword" name="cpassword" onClick={passwordChangeHandler}></input>
            <button onClick={sendSignin}>Submit</button><bk/>
        </form>


        )
}
