            <label for="input">Trip Destination:</label>
            <input type="text" id="input" name="input"></input><br></br> */}
            {/* {trips} */}
            {Trips.map(trip => 
                <div>
                    <p>this a p</p>
                    <p>{trip.destination_add}</p>
                    <p>{trip.start_add}</p>
                    <p>{trip.distance}</p>
                    <p>{trip.weather}</p>
                    <p>{trip.vehicle_id}</p>
                    <p>{trip.user_id}</p>
                </div>)}

        </div>

    );


}

const condition = authUser => !!authUser;

export default withAuthorization(condition)(TripsPage);
