        navigator.geolocation.getCurrentPosition((position) => {
            let coordinates = position.coords;
            setCurrentLocation(coordinates);
            console.log(coordinates)
        }, (err) => {
            console.warn(`Error(${err.code}): ${err.message}`);
        }, {
            enableHighAccuracy: true,
            timeout: 5000,
            maximumAge: 0
        });
    }, []);

