                case "oneCall":
                    APICall = `https://api.openweathermap.org/data/2.5/weather?lat=40.71&lon=-74.00&dt=20215101619&units=imperial&lang=en&appid=6693af864d042d45210a62d91db9b718`;

                    break;

                case "reverseGeocoding":
                    APICall = `https://maps.googleapis.com/maps/api/geocode/json?latlng=40.71,-74.00&key=AIzaSyC98mWMfSiEOhKrhwhN04WW2dKb_2lT_QY`;

                    break;

                default:
                    APICall = "";
                    break;

            };


            let response = await fetch(APICall);
            let data = await response.json();
            APIName === "reverseGeocoding" ?
            stateFunction(data.results[0].address_components) :
            stateFunction(data);
            console.log(data);
        };


        if (typeof currentLocation.latitude != "undefined") {
            fetchData("oneCall", currentLocation, setWeather);
            fetchData("reverseGeocoding", currentLocation, setUserLocation);
        };


