import React, { useContext, useState, useEffect } from 'react';
import { FirebaseContext } from '../Firebase';
import { AuthUserContext, withAuthorization } from '../Session';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import DateFnsUtils from '@date-io/date-fns';
import { MuiPickersUtilsProvider, Calendar } from "@material-ui/pickers";
import ruLocale from "date-fns/locale/ru";
import './addtrip.css';
import { Paper } from '@material-ui/core';





function AddTrip() {
    const[starting, setStarting] = useState("");
    const[destination, setDestination] = useState("");
    const[distance, setDistance] = useState("");
    const[weather, setWeather] = useState("");
    const[start_date, setStartDate] = useState("");
    const[end_date, setEndDate] = useState("")
    const[vehicle_id, setVehicleId] = useState("");
    const[success, setSuccess] = useState(true)



    const[vehicles, setVehicles] = useState([]);

    const useStyles = makeStyles((theme) => ({
        root: {
          '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '25ch',
          },
        },
      }));

    function MyCalendar() {
        const [selectedDate, setSelectedDate] = useState(new Date());


        const handleDateChange = (date) => {
            setSelectedDate(date);
        }
    };



    //   const classes = useStyles();
    //   const [value, setStarting] = React.useState('Starting');

    //   const handleChange = (event) => {
    //     setValue(event.target.value);
    //   };


    const userId = useContext(FirebaseContext).auth.currentUser.uid

    async function getVehicles() {
        console.log(userId)
        const configs = {
            method: 'post',
            body: JSON.stringify({"user_id": userId}),
            headers: {"Content-Type": "application/json"}
        }
        const response = await fetch("http://localhost:5000/api/users_vehicle", configs);
        const userVehicles = await response.json();
        console.log(userVehicles)
        setVehicles(userVehicles)
        setVehicleId(userVehicles[0][0]);
    }



    async function addTrip() {
        const body = JSON.stringify({
            starting: starting, 
            destination: destination,
            distance: distance,
            weather: weather,
            start_date: new Date(start_date).getTime(),
            end_date: new Date(end_date).getTime(),
            vehicle_id: vehicle_id,
            user_id: userId

        })
        const configs = {
            method: 'post',
            body: body,
            headers: {"Content-Type": "application/json"}
        }
        const response = await fetch("http://localhost:5000/api/trip_add", configs);
        const success = await response.json();
        setSuccess(success.status);

    }


    useEffect (() => getVehicles(), []);

    return (

        <div className="container4">
            <div className="example-parent">
            <div>
                <h2>Add a New Trip</h2>
                    <form classname="vehicleform">
                        <div>
                            <TextField id="outlined-multiline-flexible"
                            multiline
                            rowsMax={4}
                            value={starting}
                            variant="outlined"
                            type="text" 
                            label="Starting" 
                            onChange={e => setStarting(e.target.value)}>

                            </TextField>
                            <TextField
                                id="outlined-multiline-flexible"
                                multiline
                                rowsMax={4}
                                value={destination}
                                variant="outlined"
                                type="text" 
                                label="Destination" 
                                onChange={e => setDestination(e.target.value)}>
                            </TextField>
                            <TextField
                                id="outlined-multiline-flexible"
                                multiline
                                rowsMax={4}
                                value={distance}
                                variant="outlined"
                                type="text" 
                                label="Distance" 
                                onChange={e => setDistance(e.target.value)}>
                            </TextField>
                            <TextField
                                id="outlined-multiline-flexible"
                                multiline
                                rowsMax={4}
                                value={weather}
                                variant="outlined"
                                type="text" 
                                label="Weather" 
                                onChange={e => setWeather(e.target.value)}>
                            </TextField>
                        </div>
                        <div> 
                            <TextField>
                                <MuiPickersUtilsProvider utils={DateFnsUtils} locale={ruLocale}>
                                    <Paper style={{ overflow: "hidden"}}>
                                        <Calendar
                                        date={selectedDate}
                                        onChange={handleDateChange}
                                        />

                                    </Paper>
                                </MuiPickersUtilsProvider>
                            </TextField>
                            <TextField>
                                <input 
                                type="date" 
                                placeholder="End Date" 
                                onChange={e => setEndDate(e.target.value)}
                                />
                            </TextField>
                            <TextField>
                                <select value={vehicles[0] && vehicles[0][0]}
                                onChange={e => (e.target.value)}>
                                    {vehicles.map(v => <option value={v[0]}>{v[1].make}&nbsp;{v[1].model}</option>)}
                                </select>
                                </TextField>
                            <TextField>
                                <button type="button" onClick={addTrip}>
                                    submit
                                </button>
                            </TextField>
                        </div>
                    </form>
            </div>
            </div>

        </div>


    );

}


const condition = authUser => !!authUser;


export default withAuthorization(condition)(AddTrip);
