import React, { useState } from 'react';
// import Login from './components/Login';

export default function Signup() {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [createUserName, setCreateUsername] = useState("");
    const [createPassword, setCreatePassword] = useState("");
    const [Address, setAddress] = useState("");
    const [milage, setMilage] = useState("");
    const [weeklyMilage, setWeeklyMilage] = useState("");


    function firstNameChangeHandler(e) {
        setFirstName(e.target.value)
    }

    function lastNameChangeHandler(e) {
        setLastName(e.target.value)
    }

    function emailChangeHandler(e) {
        setEmail(e.target.value)
    }

    function userNameChangeHandler(e) {
        setCreateUsername(e.target.value)
    }

    function passwordChangeHandler(e) {
        setCreatePassword(e.target.value)
    }

    function addressChangeHandler(e) {
        setAddress(e.target.value)
    }

    function milageChangeHandler(e){
        setMilage(e.target.value)
    }

    function weeklyChangeHandler(e){
        setWeeklyMilage(e.target.value)
    }



    const sendSignin = async () => {
        const newUserData = JSON.stringify({
            firstname: firstName,
            lastname: lastName,
            email: email,
            username: createUserName,
            password: createPassword
        })
        const config = {
            method: "POST",
            headers: {"Content_Type": "application/json"},
            body: newUserData

        }
        const response = await fetch("http://localhost:8080/api/Login", config);
        const data = await response.json()
        console.log(data); 

        }



    return (
        <form>
            <label for="fname">first name:</label>
            <input type="text" id="fname" name="fname" onClick={firstNameChangeHandler}></input><br></br>
            <label for="lname">last name:</label>
            <input type="text" id="lname" name="lname" onClick={lastNameChangeHandler}></input><br></br>
            <label for="email">email:</label>
            <input type="text" id="email" name="email" onClick={emailChangeHandler}></input><br></br>
            <label for="cname">username:</label>
            <input type="text" id="cname" name="cname" onClick={userNameChangeHandler}></input><br></br>
            <label for="cpassword">password:</label>
            <input type="text" id="cpassword" name="cpassword" onClick={passwordChangeHandler}></input><br></br>
            <label for="address">address:</label>
            <input type="text" id="address" name="address" onClick={addressChangeHandler}></input><bk></bk>
            <label for="milage">milage:</label>
            <input type="text" id="milage" name="milage" onClick={milageChangeHandler}></input><bk></bk>
            <label for="weekly">weekly:</label>
            <input type="text" id="weekly" name="weekly" onClick={weeklyChangeHandler}></input><bk></bk>
            <button onClick={sendSignin}>Submit</button>
        </form>


        )
}
