    //     res.setHeader('Access-Control-Allow-Headers',"*");
    //     res.header('Access-Control-Allow-Credentials', true);
    //     next();
    // });

    async function getTrips() {
        const configs = {
            method: 'post',
            body: JSON.stringify({"user_id": userId}),
            headers: {"Content-Type": "application/json"}
        }
        const response = await fetch("http://localhost:5000/api/users_trip", configs);
        const userTrips = await response.json();
        console.log(userTrips)
        setTrips(userTrips);
    }


    useEffect(() => {
        const googleMapScript = document.createElement('script');
        googleMapScript.src=`https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_API_KEY}&libraries=places`;
        googleMapScript.async = true;
        window.document.body.appendChild(googleMapScript);
        googleMapScript.addEventListener('load', () => {
        getLatLng();
            })
        },[]);



    async function getDirections() {
        const configs = {
            mode: 'no cors',
            headers: {"Content-Type": "application/json"}
        }
        const response = await fetch(`https://maps.googleapis.com/maps/api/distancematrix/json?origins=1235+Prospect+Ave+Brooklyn?destinations=12+Main+Street+Maplewood+NJ?departure_time=1622557978157?mode=transit?key=AIzaSyCeekjB_1EKlLksb82sVZe6riCox1jZghs`);

        const userDirections = await response.json();
        console.log(userDirections)
        setDirections(userDirections);

    }



    useEffect (() => getTrips(), []);
    // const test = trips.map(t => <p></p>)

    useEffect (() => getDirections(), []);



    return (


        <div>

            {/* <label for="input">Starting Address:</label>
            <input type="text" id="input" name="input"></input>
            <label for="input">Trip Destination:</label>
            <input type="text" id="input" name="input"></input> */}
            {/* {trips} */}
            <p>inputs for trip start destination and end destination</p>
                <p>Trip history</p>
                    <p>Map</p>



            {trips.map(trip => 
                <div>
                    <div className="grid-container">
                        <table>
                            <p id={"rcorners1"}><tr>
                                <th>Starting route:</th>
                                <th>{trip.starting}</th><bk></bk>
                                </tr><bk></bk>
                                <tr>
                                    <th>Destination:</th>
                                    <td>{trip.destination}</td>
                                </tr><bk></bk>
                                <tr>
                                    <th>Distance:</th>
                                    <td>{trip.distance}</td>
                                </tr><bk></bk>
                                <tr>
                                    <th>Weather:</th>
                                    <td>{trip.weather}</td>
                                </tr><bk></bk>
                                <tr></tr>
                                <tr>
                                    <th>Start date:</th>
                                    <td>{trip.start_date}</td>
                                </tr><bk></bk>
                                <tr>
                                    <th>End date:</th>
                                    <td>{trip.end_date}</td>
                                </tr><bk></bk>

                                </p>
                        </table>
                    </div>
                </div>)}

        </div>

    );


}

const condition = authUser => !!authUser;

export default withAuthorization(condition)(TripsPage);
